CREATE OR REPLACE FUNCTION cm_calcular_validade_conj (nr_seq_equip_p bigint, nr_seq_embalagem_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
nr_seq_metodo_w		bigint;
qt_dias_val_w		integer;
qt_hor_val_w		integer;


BEGIN

select	nr_seq_classif
into STRICT	nr_seq_metodo_w
from  	cm_equipamento
where	nr_sequencia = nr_seq_equip_p;

select	coalesce(max(coalesce(qt_dia_validade,0)),0),
	coalesce(max(coalesce(qt_hor_validade,0)),0)
into STRICT	qt_dias_val_w,
	qt_hor_val_w
from	cm_validade
where	nr_seq_embalagem 	= nr_seq_embalagem_p
and	nr_seq_metodo		= nr_seq_metodo_w;

if (qt_dias_val_w > 0 or qt_hor_val_w > 0) then
	begin
	select	to_date(to_char(clock_timestamp() + qt_dias_val_w + (qt_hor_val_w / 24),'dd/mm/yyyy hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_retorno_w
	;
	end;
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
