CREATE OR REPLACE FUNCTION cm_obter_cod_externo_item ( nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN

select	cd_codigo
into STRICT	ds_retorno_w
from	cm_conjunto_item
where	nr_seq_item = nr_seq_item_p
and	nr_seq_conjunto = (SELECT	max(nr_seq_conjunto)
			from	cm_conjunto_item
			where	nr_seq_item = nr_seq_item_p
			and	(cd_codigo IS NOT NULL AND cd_codigo::text <> '')
			and	coalesce(nr_seq_conj_fixo::text, '') = ''
			and	coalesce(nr_seq_item_fixo::text, '') = '');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
