SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_cota_disponivel ( nr_requisicao_p bigint, nr_seq_item_req_p bigint, qt_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w			bigint;
qt_conjunto_regra_w		bigint;
ie_periodo_w			varchar(1);
cd_setor_atendimento_w		integer;
cd_estabelecimento_w		smallint;
qt_conj_atendido_w		bigint;
ie_retorno_w			varchar(1);
nr_seq_conjunto_w		bigint;


BEGIN

ie_retorno_w	:= 'S';

select	nr_seq_conjunto
into STRICT	nr_seq_conjunto_w
from	cm_requisicao_item
where	nr_sequencia = nr_seq_item_req_p;

select	cd_setor_atendimento,
	cd_estabelecimento
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	cm_requisicao
where	nr_sequencia = nr_requisicao_p;

select	count(*),
	coalesce(max(qt_conjunto),0),
	coalesce(max(ie_periodo),'D')
into STRICT	qt_regra_w,
	qt_conjunto_regra_w,
	ie_periodo_w
from	cm_conjunto_cota
where	nr_seq_conjunto 		= nr_seq_conjunto_w
and	cd_estabelecimento 	= cd_estabelecimento_w
and	cd_setor_atendimento	= cd_setor_atendimento_w
and	ie_situacao = 'A';

if (qt_regra_w > 0) then
	begin

	if (ie_periodo_w = 'D') then
		begin

		select	count(*)
		into STRICT	qt_conj_atendido_w
		from	cm_requisicao_conj a,
			cm_conjunto_cont b,
			cm_requisicao_item c,
			cm_requisicao d
		where	trunc(a.dt_atendimento) = trunc(clock_timestamp())
		and	b.nr_sequencia = a.nr_seq_conj_real
		and	c.nr_sequencia = a.nr_seq_item_req
		and	d.nr_sequencia = c.nr_seq_requisicao
		and	coalesce(b.ie_situacao,'A') = 'A'
		and	b.nr_seq_conjunto = nr_seq_conjunto_w
		and	d.cd_setor_atendimento = cd_setor_atendimento_w
		and	d.cd_estabelecimento = cd_estabelecimento_w;

		if	((qt_conj_atendido_w + coalesce(qt_conjunto_p,0)) > qt_conjunto_regra_w) then
			ie_retorno_w	:= 'N';
		end if;

		end;
	elsif (ie_periodo_w = 'M') then
		begin

		select	count(*)
		into STRICT	qt_conj_atendido_w
		from	cm_requisicao_conj a,
			cm_conjunto_cont b,
			cm_requisicao_item c,
			cm_requisicao d
		where	trunc(a.dt_atendimento,'mm') = trunc(clock_timestamp(),'mm')
		and	b.nr_sequencia = a.nr_seq_conj_real
		and	c.nr_sequencia = a.nr_seq_item_req
		and	d.nr_sequencia = c.nr_seq_requisicao
		and	coalesce(b.ie_situacao,'A') = 'A'
		and	b.nr_seq_conjunto = nr_seq_conjunto_w
		and	d.cd_setor_atendimento = cd_setor_atendimento_w
		and	d.cd_estabelecimento = cd_estabelecimento_w;

		if	((qt_conj_atendido_w + coalesce(qt_conjunto_p,0)) > qt_conjunto_regra_w) then
			ie_retorno_w	:= 'N';
		end if;

		end;
	end if;
	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
