CREATE OR REPLACE FUNCTION cm_obter_estab_atual_conj ( nr_seq_conjunto_p bigint, cd_estabelecimento_p bigint default null) RETURNS bigint AS $body$
DECLARE


cd_retorno_w		smallint;
nr_sequencia_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	cm_conjunto_movto
where	nr_seq_conjunto = nr_seq_conjunto_p;

select	coalesce(max(cd_estab_atual),0)
into STRICT	cd_retorno_w
from	cm_conjunto_movto
where	nr_sequencia = nr_sequencia_w;

if (coalesce(cd_retorno_w,0) = 0) then

	if (coalesce(cd_estabelecimento_p::text, '') = '') then

		select	cd_estabelecimento
		into STRICT	cd_retorno_w
		from	cm_conjunto_cont
		where	nr_sequencia = nr_seq_conjunto_p;
	else
		cd_retorno_w	:= cd_estabelecimento_p;

	end if;


end if;

return	cd_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
