SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_estab_origem_conj ( nr_seq_conjunto_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		integer;
ds_retorno_w			varchar(255);


BEGIN

select	coalesce(max(cd_estab_origem),0)
into STRICT	cd_estabelecimento_w
from	cm_conjunto_movto
where	nr_seq_conjunto = nr_seq_conjunto_p;

if (cd_estabelecimento_w > 0) then
	begin

	if (ie_opcao_p = 'D') then
		begin

		select	ds_fantasia
		into STRICT	ds_retorno_w
		from	estabelecimento_v
		where	cd_estabelecimento = cd_estabelecimento_w;

		end;
	else
		ds_retorno_w	:= to_char(cd_estabelecimento_w);
	end if;

	end;
else
	begin

	select	a.cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	cm_conjunto a,
		cm_conjunto_cont b
	where	a.nr_sequencia = b.nr_seq_conjunto
	and	b.nr_sequencia = nr_seq_conjunto_p;

	if (ie_opcao_p = 'D') then
		begin

		select	ds_fantasia
		into STRICT	ds_retorno_w
		from	estabelecimento_v
		where	cd_estabelecimento = cd_estabelecimento_w;

		end;
	else
		ds_retorno_w	:= to_char(cd_estabelecimento_w);
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
