SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_item_reserva_disp ( cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_retorno_w		varchar(40);
nr_seq_conjunto_w	varchar(40);


BEGIN

select	min(b.nr_sequencia)
into STRICT	nr_retorno_w
from	cm_conjunto_item a,
	cm_item b,
	cm_conjunto c
where	a.nr_seq_item = b.nr_sequencia
and	a.nr_seq_conjunto = c.nr_sequencia
and	c.cd_estabelecimento = cd_estabelecimento_p
and	b.ie_situacao = 'A'
and	coalesce(a.nr_seq_item_fixo::text, '') = ''
and	coalesce(a.nr_seq_conj_fixo::text, '') = ''
and	coalesce(c.ie_reserva,'N') = 'S'
and not exists (SELECT  1
                from    cm_conjunto_item_sub x
                where   x.nr_seq_item_sub = a.nr_seq_item
                and     x.nr_seq_conjunto_sub = a.nr_seq_conjunto
                and     coalesce(x.dt_final_vigencia::text, '') = '');

if (coalesce(nr_retorno_w,'0') = '0') then
	nr_retorno_w	:= 0;
else
	begin

	select	max(nr_seq_conjunto)
	into STRICT	nr_seq_conjunto_w
	from	cm_conjunto_item
	where	nr_seq_item = nr_retorno_w;

	nr_retorno_w	:= nr_retorno_w || ',' || nr_seq_conjunto_w;

	end;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
