SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_lista_nf_transf ( nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_lote_transf_w	bigint;
nr_nota_fiscal_w		nota_fiscal.nr_nota_fiscal%type;
ds_lista_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_seq_lote_transf
from	cm_lote_transf_conj
where	nr_seq_conjunto = nr_seq_conjunto_p;

c02 CURSOR FOR
SELECT	nr_nota_fiscal
from	nota_fiscal
where	nr_seq_transf_cme = nr_seq_lote_transf_w
and	ie_situacao not in (2,3,9)
group by nr_nota_fiscal;


BEGIN
open c01;
loop
fetch c01 into
	nr_seq_lote_transf_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	open c02;
	loop
	fetch c02 into
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		ds_lista_w	:= substr(ds_lista_w || nr_nota_fiscal_w || ', ',1,255);
		end;
	end loop;
	close c02;
	end;

end loop;
close c01;

if (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then
	ds_retorno_w	:= substr(ds_lista_w,1,length(ds_lista_w) - 2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
