CREATE OR REPLACE FUNCTION cm_obter_lote_ciclo ( nr_equipamento_p bigint, nr_seq_ciclo_p bigint, nr_ciclo_controle_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


ie_permite_ciclo_w		varchar(1);
ie_forma_inc_lote_w		varchar(2);
qt_zeros_esquerda_w		bigint;
ds_retorno_w			integer := 0;


BEGIN
ie_permite_ciclo_w := obter_param_usuario(406, 41, obter_perfil_ativo, nm_usuario_p, 0, ie_permite_ciclo_w);
ie_forma_inc_lote_w := obter_param_usuario(406, 48, obter_perfil_ativo, nm_usuario_p, 0, ie_forma_inc_lote_w);

if (ie_forma_inc_lote_w <> 'N') and (ie_permite_ciclo_w = 'S') then
	begin

	select	coalesce(max(qt_zeros_esquerda),0)
	into STRICT	qt_zeros_esquerda_w
	from	cm_equipamento
	where	nr_sequencia = nr_equipamento_p;

	if (ie_forma_inc_lote_w = 'S') then
		select	lpad((coalesce(max(somente_numero(a.cd_ciclo)),0) + 1),(length(coalesce(max(somente_numero(a.cd_ciclo)),0) + 1) + qt_zeros_esquerda_w),0)
		into STRICT	ds_retorno_w
		from	cm_ciclo a
		where	a.nr_seq_equipamento = nr_equipamento_p
		and	a.nr_sequencia =	(SELECT	max(x.nr_sequencia)
						from	cm_ciclo x
						where	x.nr_seq_equipamento = a.nr_seq_equipamento
						and	x.nr_sequencia <> nr_seq_ciclo_p);

	elsif (ie_forma_inc_lote_w = 'D') then
		select	lpad((coalesce(max(somente_numero(cd_ciclo)),0) + 1),(length(coalesce(max(somente_numero(cd_ciclo)),0) + 1) + qt_zeros_esquerda_w),0)
		into STRICT	ds_retorno_w
		from	cm_ciclo
		where	nr_seq_equipamento = nr_equipamento_p
		and	trunc(dt_atualizacao_nrec) = trunc(clock_timestamp());

	elsif (ie_forma_inc_lote_w = 'M') then
		select	lpad((coalesce(max(somente_numero(cd_ciclo)),0) + 1),(length(coalesce(max(somente_numero(cd_ciclo)),0) + 1) + qt_zeros_esquerda_w),0)
		into STRICT	ds_retorno_w
		from	cm_ciclo
		where	nr_seq_equipamento = nr_equipamento_p
		and	trunc(dt_atualizacao_nrec,'MM') = trunc(clock_timestamp(),'MM');

	elsif (ie_forma_inc_lote_w = 'SE') then
		select	lpad((coalesce(max(somente_numero(a.cd_ciclo)),0) + 1),(length(coalesce(max(somente_numero(a.cd_ciclo)),0) + 1) + qt_zeros_esquerda_w),0)
		into STRICT	ds_retorno_w
		from	cm_ciclo a
		where	a.nr_sequencia =	(SELECT max(x.nr_sequencia)
						from	cm_ciclo x
						where	x.nr_seq_equipamento = a.nr_seq_equipamento
						and	x.nr_sequencia <> nr_seq_ciclo_p)
		and	trunc(a.dt_atualizacao_nrec) = trunc(clock_timestamp());

	elsif (ie_forma_inc_lote_w = 'SN') then
		select	lpad((coalesce(max(somente_numero(a.cd_ciclo)),0) + 1),(length(coalesce(max(somente_numero(a.cd_ciclo)),0) + 1) + qt_zeros_esquerda_w),0)
		into STRICT	ds_retorno_w
		from	cm_ciclo a
		where	nr_seq_equipamento = nr_equipamento_p
		and	nr_ciclo_controle = nr_ciclo_controle_p;

	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
