SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_metodo_ciclo ( nr_seq_ciclo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_equipamento_w	bigint;
nr_seq_classif_w		bigint;


BEGIN

select	max(coalesce(nr_seq_classif,0)),
	nr_seq_equipamento
into STRICT	nr_seq_classif_w,
	nr_seq_equipamento_w
from	cm_ciclo
where	nr_sequencia = nr_seq_ciclo_p
group by nr_seq_equipamento;

if (nr_seq_classif_w <> 0) then
	begin

	select	ds_classificacao
	into STRICT	ds_retorno_w
	from	cm_classif_metodo
	where	nr_sequencia = nr_seq_classif_w;

	end;
else
	begin

	select	nr_seq_classif
	into STRICT	nr_seq_classif_w
	from	cm_equipamento
	where	nr_sequencia = nr_seq_equipamento_w;

	select	ds_classificacao
	into STRICT	ds_retorno_w
	from	cm_classif_metodo
	where	nr_sequencia = nr_seq_classif_w;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
