CREATE OR REPLACE FUNCTION cm_obter_nota_envio_ext ( nr_seq_envio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_nota_fiscal_w	varchar(255);
ds_lista_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_nota_fiscal
from	nota_fiscal
where	nr_seq_cme_envio_ext = nr_seq_envio_p
group by	nr_nota_fiscal;


BEGIN

open c01;
loop
fetch c01 into
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_lista_w	:= substr(ds_lista_w || nr_nota_fiscal_w || ', ',1,255);

	end;
end loop;
close c01;

if (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then
	ds_retorno_w	:= substr(ds_lista_w,1,length(ds_lista_w) - 2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
