CREATE OR REPLACE FUNCTION cm_obter_nota_transferencia ( nr_seq_lote_transf_p bigint, ie_transf_receb_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint;
nr_sequencia_w		bigint;
nr_nota_fiscal_w		bigint;


BEGIN

if (ie_transf_receb_p = 'T') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	nota_fiscal
	where	nr_seq_transf_cme = nr_seq_lote_transf_p
	and	ie_tipo_nota = 'SE'
	and	ie_situacao = 1;

	if (nr_sequencia_w > 0) then
		begin

		select	nr_nota_fiscal_w
		into STRICT	nr_nota_fiscal_w
		from	nota_fiscal
		where	nr_sequencia = nr_sequencia_w;

		if (ie_opcao_p = 'N') then
			nr_retorno_w	:= nr_nota_fiscal_w;
		else
			nr_retorno_w	:= nr_sequencia_w;
		end if;

		end;
	end if;

	end;
elsif (ie_transf_receb_p = 'R') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	nota_fiscal
	where	nr_seq_transf_cme = nr_seq_lote_transf_p
	and	ie_tipo_nota = 'SE'
	and	ie_situacao = 1;

	if (nr_sequencia_w > 0) then
		begin

		select	nr_nota_fiscal_w
		into STRICT	nr_nota_fiscal_w
		from	nota_fiscal
		where	nr_sequencia = nr_sequencia_w;

		if (ie_opcao_p = 'N') then
			nr_retorno_w	:= nr_nota_fiscal_w;
		else
			nr_retorno_w	:= nr_sequencia_w;
		end if;

		end;
	end if;

	end;
end if;

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
