CREATE OR REPLACE FUNCTION cm_obter_req_conjunto (nr_seq_conj_real_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_requisicao_w 	bigint;			
			
			

BEGIN

if (coalesce(nr_seq_conj_real_p,0) > 0) then

	select	max(a.nr_seq_requisicao)
	into STRICT	nr_seq_requisicao_w	
	from	cm_requisicao_item a,
