CREATE OR REPLACE FUNCTION cm_obter_se_apenas_descartavel (nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_descartavel_w		varchar(1);

c01 CURSOR FOR
SELECT 	coalesce(ie_descartavel,'N')
from   	cm_item a,
       	cm_conjunto_item b
where 	a.nr_sequencia = b.nr_seq_item
and   	b.nr_seq_conjunto = nr_seq_conjunto_p;


BEGIN

ds_retorno_w	:= 'S';

open c01;
loop
fetch c01 into
	ie_descartavel_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ie_descartavel_w = 'N') then
		ds_retorno_w	:= 'N';
	end if;

	end;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
