CREATE OR REPLACE FUNCTION cm_obter_se_ciclo_cancelado ( nr_seq_ciclo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_tipo_movto_w		varchar(15);


BEGIN

select	ie_tipo_movto
into STRICT	ie_tipo_movto_w
from	cm_ciclo_movto a
where	nr_seq_ciclo = nr_seq_ciclo_p
and	nr_sequencia = 	(SELECT	max(x.nr_sequencia)
			from	cm_ciclo_movto x
			where	x.nr_seq_ciclo = a.nr_seq_ciclo);

if (ie_tipo_movto_w = 'C') then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
