CREATE OR REPLACE FUNCTION cm_obter_se_conj_ciclo_inic ( nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_existe_w		bigint	:= 0;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cm_conjunto_cont a,
	cm_ciclo b
where	a.nr_sequencia = nr_seq_conjunto_p
and	a.nr_seq_ciclo = b.nr_sequencia
and	(b.dt_inicio IS NOT NULL AND b.dt_inicio::text <> '');

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

end;
