CREATE OR REPLACE FUNCTION cm_obter_se_conj_completo ( nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'S';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cm_item_cont
where	nr_seq_conjunto = nr_seq_conjunto_p
and	coalesce(ie_excluido,'N') = 'N'
and	ie_status not in ('1','2');

if (qt_existe_w > 0) then
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
