SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_se_conj_cont_manut ( nr_seq_conj_cont_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_existe_w		bigint;
nr_seq_conjunto_w	cm_conjunto_cont.nr_seq_conjunto%type;


BEGIN

if (coalesce(nr_seq_conj_cont_p,0) <> 0) then
	begin

	select	nr_seq_conjunto
	into STRICT	nr_seq_conjunto_w
	from	cm_conjunto_cont
	where	nr_sequencia = nr_seq_conj_cont_p;

	select	count(*)
	into STRICT	qt_existe_w
	from	cm_manutencao_conj
	where	nr_seq_conjunto = nr_seq_conjunto_w
	and	coalesce(dt_retorno::text, '') = '';

	if (qt_existe_w > 0) then
		ie_retorno_w	:= 'S';
	else
		ie_retorno_w	:= 'N';
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
