SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_se_conj_em_transf ( nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cm_lote_transf_conj a,
	cm_lote_transferencia b
where	a.nr_seq_lote_transf = b.nr_sequencia
and	a.nr_seq_conjunto = nr_seq_conjunto_p
and	coalesce(b.dt_baixa::text, '') = '';

if (qt_existe_w > 0) then
	ie_retorno_w	:= 'S';
else
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
