SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_se_consiste_incluir ( nr_seq_conjunto_p bigint, nr_requisicao_p bigint, ie_permite_dif_p text, ie_permite_maior_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
nr_seq_conj_ester_w	bigint;
qt_conj_nao_atend_w	bigint;
qt_conjunto_igual_w	bigint;


BEGIN

select	nr_seq_conjunto
into STRICT	nr_seq_conj_ester_w
from	cm_conjunto_cont
where	nr_sequencia = nr_seq_conjunto_p;

select	count(*)
into STRICT	qt_conj_nao_atend_w
from	cm_requisicao_item
where	nr_seq_requisicao = nr_requisicao_p
and	nr_seq_conjunto = nr_seq_conj_ester_w
and	coalesce(cd_motivo_baixa::text, '') = '';

select	count(a.nr_sequencia)
into STRICT	qt_conjunto_igual_w
from	cm_requisicao_item a,
	cm_requisicao b
where	nr_seq_requisicao = nr_requisicao_p
and	b.nr_sequencia = a.nr_seq_requisicao
and	nr_seq_conjunto = nr_seq_conj_ester_w
and	a.dt_atualizacao_nrec < b.dt_liberacao;

if (qt_conj_nao_atend_w <> 0) then
	ie_retorno_w	:= 'N';
else
	begin

	if (qt_conjunto_igual_w = 0) and (ie_permite_dif_p = 'N') then
		ie_retorno_w	:= 'N';
	elsif (ie_permite_maior_p = 'N') and (ie_permite_dif_p = 'N') then
		ie_retorno_w	:= 'N';
	elsif (qt_conjunto_igual_w <> 0) and (ie_permite_maior_p = 'N') then
		ie_retorno_w	:= 'N';
	else
		ie_retorno_w	:= 'S';
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
