SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_se_existe_cota ( nr_requisicao_p bigint, nr_seq_conjunto_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w			bigint;
cd_setor_atendimento_w		integer;
cd_estabelecimento_w		smallint;
ie_retorno_w			varchar(1);


BEGIN

ie_retorno_w	:= 'N';

select	cd_setor_atendimento,
	cd_estabelecimento
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	cm_requisicao
where	nr_sequencia = nr_requisicao_p;

select	count(*)
into STRICT	qt_regra_w
from	cm_conjunto_cota
where	nr_seq_conjunto 		= nr_seq_conjunto_p
and	cd_estabelecimento 	= cd_estabelecimento_w
and	cd_setor_atendimento	= cd_setor_atendimento_w
and	ie_situacao = 'A';

if (qt_regra_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
