SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_se_item_substituto ( nr_seq_conjunto_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cm_conjunto_item_sub a
where	nr_seq_conjunto = nr_seq_conjunto_p
and	nr_seq_item_sub = nr_seq_item_p
and	coalesce(dt_final_vigencia::text, '') = ''
and	exists (SELECT	1
		from	cm_conjunto_item x,
			cm_conjunto y
		where	x.nr_seq_item = a.nr_seq_item_sub
		and	y.nr_sequencia = x.nr_seq_conjunto
		and	coalesce(y.ie_reserva,'N') = 'S'
		and	coalesce(nr_seq_conj_fixo::text, '') = ''
		and	coalesce(nr_seq_item_fixo::text, '') = '');

ds_retorno_w	:= 'N';
if (qt_existe_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
