SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cm_obter_se_req_pend_retorno ( nr_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ds_retorno_w	varchar(1)	:= 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cm_requisicao_conj a,
	cm_requisicao_item b
where	a.nr_seq_item_req	= b.nr_sequencia
and	b.nr_seq_requisicao	= nr_requisicao_p
and	coalesce(a.dt_retorno::text, '') = '';

if (qt_existe_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
