CREATE OR REPLACE FUNCTION cm_obter_se_retira_ciclo ( nr_seq_ciclo_p bigint, ie_opcao_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'S';
cd_estabelecimento_w	integer;
nr_parametro_w		integer;
ie_permite_retirar_w	varchar(1);

/*
Opções:
M = Manual;
B = Barras.

Retorno:
S = Sim;
N = Não;
A = Avisa.
*/
BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	cm_ciclo
where	nr_sequencia = nr_seq_ciclo_p;

if (ie_opcao_p = 'M') then
	nr_parametro_w	:= 10;
elsif (ie_opcao_p = 'B') then
	nr_parametro_w	:= 128;
end if;

ie_permite_retirar_w	:= obter_valor_param_usuario(406,nr_parametro_w, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);

if (ie_permite_retirar_w = 'S') then
	ie_retorno_w	:= 'S';
elsif (ie_permite_retirar_w = 'U') then
	ie_retorno_w	:= 'N';
elsif (ie_permite_retirar_w = 'N') then
	begin

	select	CASE WHEN coalesce(dt_fim::text, '') = '' THEN 'S'  ELSE 'N' END
	into STRICT	ie_retorno_w
	from	cm_ciclo
	where	nr_sequencia = nr_seq_ciclo_p;

	end;
elsif (ie_permite_retirar_w = 'C') then
	begin

	select	CASE WHEN coalesce(dt_fim::text, '') = '' THEN CASE WHEN coalesce(dt_cancelamento::text, '') = '' THEN 'S'  ELSE 'N' END   ELSE 'N' END
	into STRICT	ie_retorno_w
	from	cm_ciclo
	where	nr_sequencia = nr_seq_ciclo_p;

	end;
elsif (ie_permite_retirar_w = 'A') or (ie_permite_retirar_w = 'B') then
	begin

	select	CASE WHEN coalesce(dt_fim::text, '') = '' THEN 'S'  ELSE 'N' END
	into STRICT	ie_retorno_w
	from	cm_ciclo
	where	nr_sequencia = nr_seq_ciclo_p;

	if (ie_retorno_w = 'S') then
		begin

		select	CASE WHEN coalesce(dt_inicio::text, '') = '' THEN 'S'  ELSE 'A' END
		into STRICT	ie_retorno_w
		from	cm_ciclo
		where	nr_sequencia = nr_seq_ciclo_p;

		end;
	end if;

	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
