CREATE OR REPLACE FUNCTION cns_obter_cod_estrut ( nr_seq_area_p bigint, nr_seq_micro_area_p bigint, nr_seq_familia_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN
begin
if (nr_seq_familia_p IS NOT NULL AND nr_seq_familia_p::text <> '') then
