	begin
	select	cd_micro_area
	into STRICT	ds_retorno_w
	from	psf_micro_area
	where	nr_sequencia	= nr_seq_micro_area_p;
	end;
elsif (nr_seq_area_p IS NOT NULL AND nr_seq_area_p::text <> '') then
	begin
	select	cd_area
	into STRICT	ds_retorno_w
	from	psf_area
	where	nr_sequencia	= nr_seq_area_p;
	end;
end if;
exception
	when others then
		ds_retorno_w	:= 0;
end;

return	ds_retorno_w;

