CREATE OR REPLACE FUNCTION cns_obter_dados_domicilio ( nr_sequencia_p bigint, ie_opcao_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
	L - Logradouro
	P - Programa cobertura
	C - Cadastrador
*/
/* IE_TIPO_P
	C - Codigo
	D - Descricao
*/
nr_seq_logradouro_w	bigint;
nr_seq_programa_w	bigint;
nr_seq_cadastrador_w	bigint;
cd_ret_w		bigint;
ds_ret_w		varchar(80);
ds_retorno_w		varchar(80);


BEGIN

select	nr_seq_tipo_logradouro,
	nr_seq_programa,
	nr_seq_cadastrador
into STRICT	nr_seq_logradouro_w,
	nr_seq_programa_w,
	nr_seq_cadastrador_w
from	cns_domicilio
where	nr_sequencia	= nr_Sequencia_p;

/* Tipo logradouro */

if (ie_opcao_p	= 'L') then
	begin
	select	cd_tipo_logradouro,
		ds_tipo_logradouro
	into STRICT	cd_ret_w,
		ds_ret_w
	from	cns_tipo_logradouro
	where	nr_sequencia	= nr_seq_logradouro_w;
	end;
/* Programa cobertura */

elsif (ie_opcao_p	= 'P') then
	begin
	select	cd_programa_cobertura,
		ds_programa_cobertura
	into STRICT	cd_ret_w,
		ds_ret_w
	from	cns_programa_cobertura
	where	nr_sequencia	= nr_seq_programa_w;
	end;
/* Cadastrador */

elsif (ie_opcao_p	= 'C') then
	begin
	select	cd_cadastrador,
		nm_cadastrador
	into STRICT	cd_ret_w,
		ds_ret_w
	from	cns_cadastrador
	where	nr_sequencia	= nr_seq_cadastrador_w;
	end;
end if;

if (ie_tipo_p	= 'C') then
	ds_retorno_w	:= cd_ret_w;
elsif (ie_tipo_p	= 'D') then
	ds_retorno_w	:= ds_ret_w;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
