CREATE OR REPLACE FUNCTION cns_obter_desc_estrut ( cd_area_p bigint, cd_micro_area_p bigint, cd_familia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);


BEGIN
begin
if (cd_familia_p IS NOT NULL AND cd_familia_p::text <> '') then
	begin
	select	ds_familia
	into STRICT	ds_retorno_w
	from	psf_familia
	where	cd_familia	= cd_familia_p;
	end;
elsif (cd_micro_area_p IS NOT NULL AND cd_micro_area_p::text <> '') then
	begin
	select	ds_micro_area
	into STRICT	ds_retorno_w
	from	psf_micro_area
	where	cd_micro_area	= cd_micro_area_p;
	end;
elsif (cd_area_p IS NOT NULL AND cd_area_p::text <> '') then
	begin
	select	ds_area
	into STRICT	ds_retorno_w
	from	psf_area
	where	cd_area	= cd_area_p;
	end;
end if;
exception
	when others then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(306168); -- Erro estrutura
end;

return	ds_retorno_w;

END;
$body$
