SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cns_obter_domicilio_estrut (nr_seq_domicilio_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_P
	F - Familia
	M - Micro area
	A - Area
	S - Segmento
*/
cd_retorno_w		varchar(10);
nr_seq_familia_w	bigint;
nr_seq_micro_area_w	bigint;
nr_seq_area_w		bigint;
nr_seq_segmento_w	bigint;


BEGIN

select	coalesce(max(nr_seq_familia),0)
into STRICT	nr_seq_familia_w
from	cns_domicilio
where	nr_sequencia	= nr_seq_domicilio_p;

select	coalesce(max(nr_seq_micro_area),0)
into STRICT	nr_seq_micro_area_w
from	psf_familia
where	nr_sequencia	= nr_seq_familia_w;

select	coalesce(max(nr_seq_area),0)
into STRICT	nr_seq_area_w
from	psf_micro_area
where	nr_sequencia	= nr_seq_micro_area_w;

select	coalesce(max(nr_seq_segmento),0)
into STRICT	nr_seq_segmento_w
from	psf_area
where	nr_sequencia	= nr_seq_area_w;

if (ie_tipo_p	= 'F') then
	begin
	select	coalesce(max(cd_familia),0)
	into STRICT	cd_retorno_w
	from	psf_familia
	where	nr_sequencia	= nr_seq_familia_w;
	end;
elsif (ie_tipo_p	= 'M') then
	begin
	select	coalesce(max(cd_micro_area),0)
	into STRICT	cd_retorno_w
	from	psf_micro_area
	where	nr_sequencia	= nr_seq_micro_area_w;
	end;
elsif (ie_tipo_p	= 'A') then
	begin
	select	coalesce(max(cd_area),0)
	into STRICT	cd_retorno_w
	from	psf_area
	where	nr_sequencia	= nr_seq_area_w;
	end;
elsif (ie_tipo_p	= 'S') then
	begin
	select	coalesce(max(cd_segmento),0)
	into STRICT	cd_retorno_w
	from	psf_segmento
	where	nr_sequencia	= nr_seq_segmento_w;
	end;
end if;

return	cd_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cns_obter_domicilio_estrut (nr_seq_domicilio_p bigint, ie_tipo_p text) FROM PUBLIC;

