CREATE OR REPLACE FUNCTION codificacao_obter_regra ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_doenca_cid_p text default null) RETURNS bigint AS $body$
DECLARE


cd_regra_w	bigint := 0;


BEGIN

select	count(*)
into STRICT	cd_regra_w
from	codificacao_regra_grupo
where	coalesce(cd_area_proc::text, '') = ''
and		coalesce(cd_doenca_cid::text, '') = ''
and		coalesce(cd_categoria_cid::text, '') = ''
and		coalesce(cd_especialidade_cid::text, '') = ''
and		coalesce(cd_especialidade_proc::text, '') = ''
and		coalesce(cd_grupo_proc::text, '') = ''
and		coalesce(cd_procedimento::text, '') = ''
and		coalesce(ie_excludente, 'N') = 'N'
and		not exists (	SELECT	1
						from	codificacao_regra_grupo
						where	coalesce(ie_excludente, 'N') = 'S'
					);

if (cd_regra_w = 0) then

	select	count(*)
	into STRICT	cd_regra_w
	from	codificacao_regra_grupo
	where	((coalesce(cd_procedimento, 0) = 0 or cd_procedimento = cd_procedimento_p)
	and (coalesce(cd_area_proc, 0) = 0 or cd_area_proc = (obter_dados_estrut_proc(cd_procedimento_p, ie_origem_proced_p ,'C', 'A'))::numeric )
	and (coalesce(cd_especialidade_proc, 0) = 0 or cd_especialidade_proc = (obter_dados_estrut_proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'E'))::numeric )
	and (coalesce(cd_grupo_proc, 0) = 0 or cd_grupo_proc = (obter_dados_estrut_proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'G'))::numeric )
	and (coalesce(cd_doenca_cid, '0') = '0' or cd_doenca_cid = cd_doenca_cid_p)
	and		coalesce(ie_excludente, 'N') = 'N')
	and 	not exists (	SELECT	1
							from	codificacao_regra_grupo
							where	((coalesce(cd_procedimento, 0) = 0 or cd_procedimento = cd_procedimento_p)
							and (coalesce(cd_area_proc, 0) = 0 or cd_area_proc = (obter_dados_estrut_proc(cd_procedimento_p, ie_origem_proced_p ,'C', 'A'))::numeric )
							and (coalesce(cd_especialidade_proc, 0) = 0 or cd_especialidade_proc = (obter_dados_estrut_proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'E'))::numeric )
							and (coalesce(cd_grupo_proc, 0) = 0 or cd_grupo_proc = (obter_dados_estrut_proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'G'))::numeric )
							and (coalesce(cd_doenca_cid, '0') = '0' or cd_doenca_cid = cd_doenca_cid_p)
							and		coalesce(ie_excludente, 'N') = 'S'));

end if;

return cd_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
