CREATE OR REPLACE FUNCTION comunc_obter_qt_usuarios_leram (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

qt_registro_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_registro_w
from    usuario b,
	comunic_interna_lida a
where 	a.nr_sequencia = nr_sequencia_p
and     a.nm_usuario = b.nm_usuario
and     a.nm_usuario <> (	SELECT 	coalesce(nm_usuario_oculto,'Nao Oculto')
				from 	comunic_interna
				where nr_sequencia = nr_sequencia_p);


return	qt_registro_w;

end;
$body$
