SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_bsc_acerto_analista ( dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_registro_w	bigint;
pr_soma_w		double precision;
qt_retorno_w	double precision;


BEGIN
select	count(*),
	coalesce(sum(a.qt_informacao),0)
into STRICT	qt_registro_w,
	pr_soma_w
from	w_indicador_desenv a
where	a.ie_informacao = 'PRASA'
and	trunc(a.dt_referencia) = trunc(dt_referencia_p);

if (qt_registro_w = 0) then
	qt_registro_w := 1;
end if;

qt_retorno_w := (pr_soma_w / qt_registro_w);

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
