SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_bsc_obter_prospect_cont ( dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


/* variáveis*/

nr_sequencia_w		bigint;
qt_retorno_w		bigint := 0;
dt_inicial_prospect_w	timestamp;
dt_inicial_contrato_w	timestamp;
dt_final_prospect_w	timestamp;
dt_final_contrato_w	timestamp;
dt_inicial_w		timestamp;
dt_final_w		timestamp;
qt_prospect_w		bigint;
qt_contrato_w		bigint;
ds_cliente_w		varchar(4000);

/* Selecionar todos clientes*/

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	com_cliente a
where	a.ie_situacao = 'A'
and	a.ie_produto in ('O', 'P', 'M', 'MD', 'MDC', 'I');


BEGIN
dt_inicial_w	:= trunc(dt_referencia_p,'mm');
dt_final_w	:= fim_mes(dt_inicial_w);
/* Abertura do cursor que trará todos os clientes */

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_prospect_w
	from	com_cliente_log
	where	nr_seq_cliente	= nr_sequencia_w
	and	ie_classificacao = 'P';

	select	count(*)
	into STRICT	qt_contrato_w
	from	com_cliente_log
	where	nr_seq_cliente	= nr_sequencia_w
	and	ie_fase_venda = 'C';

	if (qt_prospect_w > 0) and (qt_contrato_w > 0) then
		begin

		/* Data inicial do prospect */

		select	coalesce(max(a.dt_log),clock_timestamp() + interval '1 days')
		into STRICT	dt_inicial_prospect_w
		from	com_cliente_log a
		where	a.ie_log		= 2
		and	a.ie_classificacao	= 'P'
		and	a.nr_seq_cliente	= nr_sequencia_w;
		/* Data final do prospect */

		select	coalesce(max(a.dt_log),clock_timestamp())
		into STRICT	dt_final_prospect_w
		from	com_cliente_log a
		where	a.ie_log		= 2
		and	coalesce(a.dt_log,clock_timestamp())	>= dt_inicial_prospect_w
		and	a.ie_classificacao	<> 'P'
		and	a.nr_seq_cliente	= nr_sequencia_w;
		/* Data inicial fase Contrato*/

		select	coalesce(max(a.dt_log),clock_timestamp() + interval '1 days')
		into STRICT	dt_inicial_contrato_w
		from	com_cliente_log a
		where	a.ie_log		= 1
		and	a.ie_fase_venda		= 'C'
		and	a.nr_seq_cliente	= nr_sequencia_w;
		/* Data final fase Contrato*/

		select	coalesce(max(a.dt_log),clock_timestamp())
		into STRICT	dt_final_Contrato_w
		from	com_cliente_log a
		where	a.ie_log		= 1
		and	coalesce(a.dt_log,clock_timestamp())	>= dt_inicial_contrato_w
		and	a.ie_fase_venda		<> 'C'
		and	a.nr_seq_cliente	= nr_sequencia_w;

		if (dt_inicial_prospect_w <= dt_final_w) and (dt_final_prospect_w >= dt_inicial_w) and (dt_inicial_contrato_w <= dt_final_w) and (dt_final_contrato_w >= dt_inicial_w) and
			((dt_inicial_contrato_w >= dt_inicial_prospect_w AND dt_final_contrato_w <= dt_final_prospect_w) or
			(dt_inicial_prospect_w >= dt_inicial_contrato_w AND dt_final_prospect_w <= dt_final_prospect_w)) then
			qt_retorno_w := qt_retorno_w + 1;
			ds_cliente_w	:= ds_cliente_w || chr(13) || chr(10) || nr_sequencia_w;
		end if;
		end;
	end if;
	end;
end loop;
close C01;
return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION com_bsc_obter_prospect_cont ( dt_referencia_p timestamp) FROM PUBLIC;

