SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_consiste_dados_solic (nr_seq_solic_p bigint, nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_produto_w		varchar(15);
ds_mensagem_w		varchar(255);
qt_participante_w	bigint;
qt_modulo_w		bigint;
qt_questionario_w	bigint;


BEGIN

if (coalesce(nr_seq_solic_p,0) > 0) and (coalesce(nr_seq_cliente_p,0) > 0) then

	select	count(*)
	into STRICT	qt_participante_w
	from	com_solic_sd_partic
	where	nr_seq_solic_sd = nr_seq_solic_p;

	select	count(*)
	into STRICT	qt_modulo_w
	from	com_solic_sd_mod
	where	nr_seq_solic_sd = nr_seq_solic_p;

	select	count(*)
	into STRICT	qt_questionario_w
	from	com_solic_sd_result
	where	nr_seq_solic = nr_seq_solic_p;

	ie_produto_w := com_obter_produto_cli_js(nr_seq_cliente_p);

	if	((coalesce(qt_participante_w,0) = 0) or (coalesce(qt_questionario_w,0) = 0) or
		((coalesce(qt_modulo_w,0) = 0) and (ie_produto_w <> 'I') and (ie_produto_w <> 'MDC'))) then

		ds_mensagem_w := substr(obter_texto_tasy(325602, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;
end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
