CREATE OR REPLACE FUNCTION com_get_lista_funcoes_modulo ( nr_seq_ofic_mod_p com_cli_ofic_uso_mod.nr_sequencia%type, nr_seq_idioma_p idioma.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [ X ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências: Relatórios: 4098, 4099 e 4100.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000);

c01 CURSOR FOR
	SELECT	distinct
		substr(coalesce(obter_desc_exp_idioma(f.cd_exp_funcao, nr_seq_idioma_p), ds_funcao), 1, 200)
			|| CASE WHEN coalesce(com.ie_plataforma::text, '') = '' THEN  ' - N/A'  ELSE ' - ' || obter_valor_dominio(2811, com.ie_plataforma) END  ds_funcao
	from	com_cli_ofic_uso_mod_func com,
		funcao f
	where	f.cd_funcao = com.cd_funcao
	and	com.nr_seq_mod_ofic_uso = nr_seq_ofic_mod_p
	order by 1;

BEGIN

	for	reg01 in c01
	loop
		ds_retorno_w := substr(ds_retorno_w || reg01.ds_funcao || chr(13) || chr(10), 1, 4000);
	end loop;

	ds_retorno_w := substr(ds_retorno_w, 1, length(ds_retorno_w)-2);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
