CREATE OR REPLACE FUNCTION com_get_lista_resp_modulo ( nr_seq_ofic_mod_p com_cli_ofic_uso_mod.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [ X ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências: Relatórios: 4098, 4099 e 4100.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000);

c01 CURSOR FOR
	SELECT	substr(ccoump.ds_pessoa_fisica, 1, 255) ds_pessoa
	from	com_cli_ofic_uso_mod_pes ccoump
	where	ccoump.nr_seq_mod_ofic_uso = nr_seq_ofic_mod_p
	order by 1;

BEGIN

	for	reg01 in c01
	loop
		ds_retorno_w := substr(ds_retorno_w || reg01.ds_pessoa || ',' || chr(13) || chr(10), 1, 4000);
	end loop;

ds_retorno_w := substr(ds_retorno_w, 1, length(ds_retorno_w) -3);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
