CREATE OR REPLACE FUNCTION com_obter_atividade_agenda (nr_seq_projeto_p bigint, cd_executor_p text, dt_atividade_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_atividade_w		double precision;


BEGIN
select	round((dividir(sum(b.QT_MIN_ATIV) , 60))::numeric,2)
into STRICT	qt_atividade_w
from 	proj_rat a,
	PROJ_RAT_ATIV b
where 	a.nr_sequencia = b.nr_seq_rat
and	a.cd_executor = cd_executor_p
and	a.nr_seq_proj = nr_seq_projeto_p
and	trunc(DT_INICIO_ATIV, 'dd') = dt_atividade_p;

return	coalesce(qt_atividade_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
