CREATE OR REPLACE FUNCTION com_obter_bsc_treinamento ( dt_inicial_p timestamp, dt_final_p timestamp ) RETURNS bigint AS $body$
DECLARE


qt_real_w			double precision;
qt_total_w			double precision;
qt_resultados_w		bigint;

c02 CURSOR FOR
SELECT	a.nr_seq_tipo_avaliacao,
		b.nr_seq_item,
		b.qt_resultado
from	sac_pesquisa_result b,
		sac_pesquisa a
where	a.nr_sequencia 		= b.nr_seq_pesquisa
and		a.nr_seq_tipo_avaliacao = 1495
and		(b.qt_resultado IS NOT NULL AND b.qt_resultado::text <> '')
and		b.nr_seq_item in (	18056, 18057, 18058, 18059, 18061, 18062,
							18063, 18064, 18065)
and		a.dt_avaliacao between dt_inicial_p and fim_dia(dt_final_p)
order by 1,2,3;

vet02	C02%RowType;


BEGIN
qt_total_w	:= 0;
qt_resultados_w	:= 0;

open C02;
loop
fetch C02 into
	vet02;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
		vet02.qt_resultado := vet02.qt_resultado / 2;

		qt_total_w	:= qt_total_w + vet02.qt_resultado;
		qt_resultados_w	:= qt_resultados_w +1;
	end;
end loop;
close C02;

qt_real_w	:= round((qt_total_w / qt_resultados_w)::numeric,1);

return	qt_real_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION com_obter_bsc_treinamento ( dt_inicial_p timestamp, dt_final_p timestamp ) FROM PUBLIC;

