CREATE OR REPLACE FUNCTION com_obter_cliente_abc (nr_sequencia_p bigint, dt_referencia_p timestamp, pr_curva_a_p bigint, pr_curva_b_p bigint, pr_curva_c_p bigint) RETURNS varchar AS $body$
DECLARE



ie_curva_w		varchar(01);
vl_faturamento_w	double precision;
vl_fat_cliente_w	double precision;
pr_fat_cliente_w	double precision;



BEGIN

select	coalesce(sum(a.vl_titulo),0) vl_total_faturamento
into STRICT	vl_faturamento_w
FROM com_cliente c, titulo_receber a
LEFT OUTER JOIN nota_fiscal b ON (a.nr_seq_nf_saida = b.nr_sequencia)
WHERE a.cd_cgc		= c.cd_cnpj and trunc(a.dt_vencimento,'month') = trunc(dt_referencia_p,'month');

select	sum(vl_titulo) vl_fat_cliente
into STRICT	vl_fat_cliente_w
FROM com_cliente c, titulo_receber a
LEFT OUTER JOIN nota_fiscal b ON (a.nr_seq_nf_saida = b.nr_sequencia)
WHERE a.cd_cgc 		= c.cd_cnpj and c.nr_sequencia 	= nr_sequencia_p and trunc(a.dt_vencimento,'month') = trunc(dt_referencia_p,'month');

select	dividir(vl_fat_cliente_w,vl_faturamento_w) * 100
into STRICT	pr_fat_cliente_w
;

if (pr_fat_cliente_w >= pr_curva_a_p) then
	ie_curva_w := 'A';
elsif (pr_fat_cliente_w >= pr_curva_b_p) and (pr_fat_cliente_w <  pr_curva_a_p) then
	ie_curva_w := 'B';
elsif (pr_fat_cliente_w = 0) then
	ie_curva_w := 'D';
else
	ie_curva_w := 'C';
end if;

return	ie_curva_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
