CREATE OR REPLACE FUNCTION com_obter_cliente_vinculo ( nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w			varchar(4000);
nr_seq_cliente_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	com_cliente
	where	nr_sequencia in ( 	SELECT	nr_seq_cliente_vinculo
					from	com_cliente_vinculo
					where	nr_seq_cliente = nr_seq_cliente_p)
	or	nr_sequencia = nr_seq_cliente_p
	or	nr_sequencia in (	select	nr_seq_cliente
					from	com_cliente_vinculo
					where	nr_seq_cliente_vinculo = nr_seq_cliente_p)
	or	nr_sequencia in (	select	a.nr_seq_cliente_vinculo
					from	com_cliente_vinculo a
					where	a.nr_seq_cliente in (	select	nr_seq_cliente
									from	com_cliente_vinculo
									where    nr_seq_cliente_vinculo = nr_seq_cliente_p));


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_cliente_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w := ds_retorno_w || to_char(nr_seq_cliente_w) || ',';
end loop;

ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-1);

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
