CREATE OR REPLACE FUNCTION com_obter_dados_historico ( nr_seq_historico_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* 	DC - Descrição da classificação do histórico	*/

ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p	= 'DC') then
	select	ds_classificacao
	into STRICT	ds_retorno_w
	from	com_cliente_hist a,
		com_classif_hist b
	where	a.nr_seq_classif	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_historico_p;
end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
