CREATE OR REPLACE FUNCTION com_obter_dados_lead ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*Opções:
CFA - Forma de aquisição
DFA - Descrição Forma de aquisição
*/
ds_retorno_w		varchar(255) := '';
cd_forma_aquisicao_w	varchar(15);


BEGIN

select	cd_forma_aquisicao
into STRICT	cd_forma_aquisicao_w
from	com_solic_lead
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'CFA') then
	ds_retorno_w := cd_forma_aquisicao_w;
elsif (ie_opcao_p = 'DFA') then
	ds_retorno_w := substr(obter_valor_dominio(5053,cd_forma_aquisicao_w),1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
