CREATE OR REPLACE FUNCTION com_obter_dados_mod_lic ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
D - Descrição da Modalidade
*/
ds_retorno_w		varchar(80);
ds_modalidade_w	varchar(80);


BEGIN

if (ie_opcao_p = 'D') then
	select	ds_modalidade
	into STRICT	ds_modalidade_w
	from	com_modalidade_lic
	where	nr_sequencia = nr_sequencia_p;

	ds_retorno_w	:= ds_modalidade_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
