CREATE OR REPLACE FUNCTION com_obter_dados_politica ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
C - Código da política comercial
*/
ds_retorno_w		varchar(20);
cd_politica_w		varchar(20);


BEGIN

if (ie_opcao_p = 'C') then
	select	cd_politica
	into STRICT	cd_politica_w
	from	com_politica_coml
	where	nr_sequencia = nr_sequencia_p;

	ds_retorno_w	:= cd_politica_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
