SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_dados_sd (nr_sequencia_p bigint, nr_seq_os_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);
dt_agenda_w	varchar(30);
nr_sequencia_w	bigint;


BEGIN

/*
ie_opcao_p
DA - data da agenda
NR - sequencia da agenda
*/
if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(nr_seq_os_p,0) > 0) then

	select	to_char(max(dt_agenda),'dd/mm/yyyy hh24:mi:ss'),
		max(nr_sequencia)
	into STRICT	dt_agenda_w,
		nr_sequencia_w
	from	agenda_tasy
	where 	nr_ordem_servico = nr_seq_os_p;

end if;

if (ie_opcao_p = 'DA') then
	ds_retorno_w := dt_agenda_w;
elsif (ie_opcao_p = 'NR') then
	ds_retorno_w := nr_sequencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
