CREATE OR REPLACE FUNCTION com_obter_descricao_canal (nr_seq_canal_p com_canal.nr_sequencia%type, ds_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_valor_canal_w	com_canal.nm_guerra%type;
nr_sequencia_w		com_canal.nr_sequencia%type;
nm_guerra_w		com_canal.nm_guerra%type;


BEGIN

select	max(b.nr_sequencia),
	max(b.nm_guerra)
into STRICT	nr_sequencia_w,
	nm_guerra_w
from	com_canal b
where 	nr_sequencia = nr_seq_canal_p;

if (ds_opcao_p = 'C') then
	begin
	ds_valor_canal_w := nr_sequencia_w;
	end;
else
	begin
	ds_valor_canal_w := nm_guerra_w;
	end;
end if;

return	ds_valor_canal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
