CREATE OR REPLACE FUNCTION com_obter_ds_motivo_canc (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_w	varchar(100);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	select	substr(ds_motivo,1,100)
	into STRICT	ds_motivo_w
	from	com_motivo_cancel_sd
	where	nr_sequencia = nr_sequencia_p;

end if;

return	ds_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
