CREATE OR REPLACE FUNCTION com_obter_gestor_vig ( nr_seq_cliente_p bigint, dt_ref_ini_p timestamp, dt_ref_fim_p timestamp, ie_retorno_p bigint) RETURNS varchar AS $body$
DECLARE

 
/* ie_retorno_p 
Define o tipo de retorno da function 
1 - cd_pessoa_fisica 
2 - ds_pessoa_fisica 
*/
 
 
ie_primeiro_w		boolean := true;
cd_pessoa_fisica_w	varchar(10);
ds_pessoa_fisica_w		varchar(60);
ds_retorno_w		varchar(4000) := '';

c01 CURSOR FOR 
SELECT	a.cd_pessoa_fisica, 
	obter_nome_pf(a.cd_pessoa_fisica) 
from	com_cliente_gestor a 
where	a.nr_seq_cliente	= nr_seq_cliente_p 
and	a.dt_inicial between dt_ref_ini_p and fim_dia(dt_ref_fim_p) 
and	a.dt_final between dt_ref_ini_p and fim_dia(dt_ref_fim_p);


BEGIN 
open C01;
loop 
fetch C01 into	 
	cd_pessoa_fisica_w, 
	ds_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (not ie_primeiro_w) then -- add virgula 
		ds_retorno_w := ds_retorno_w || ',';
	end if;
	if (ie_retorno_p = 1) then -- cd_pessoa_fisica 
		ds_retorno_w := ds_retorno_w || '''' || cd_pessoa_fisica_w || '''';
	elsif (ie_retorno_p = 2) then -- ds_pessoa_fisica 
		ds_retorno_w := ds_retorno_w || ds_pessoa_fisica_w;
	end if;
	if (ie_primeiro_w) then -- se primeira vez 
		ie_primeiro_w := false;
	end if;
	end;
end loop;
close C01;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
