SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_horas_prev_implant ( nr_sequencia_p bigint, nr_seq_proposta_p bigint, ie_origem_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_proposta_w	bigint;
qt_horas_w		bigint := 0;

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	if (ie_origem_p = 'P') then
		begin
		if (coalesce(nr_seq_proposta_p::text, '') = '') then
			begin
			select	max(b.nr_sequencia)
			into STRICT	nr_seq_proposta_w
			from	com_prev_implant a,
				com_cliente_proposta b
			where	b.nr_seq_cliente =	a.nr_seq_cliente
			and	a.nr_sequencia =	nr_sequencia_p
			and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
			and	coalesce(b.dt_cancelamento::text, '') = '';
			end;
		else
			begin
			nr_seq_proposta_w	:= nr_seq_proposta_p;
			end;
		end if;


		if (nr_seq_proposta_w IS NOT NULL AND nr_seq_proposta_w::text <> '') then
			begin
			select 	coalesce(sum(qt_hora),0)
			into STRICT	qt_horas_w
			from	com_cliente_prop_estim
			where	nr_seq_proposta = nr_seq_proposta_w;
			end;
		end if;
		end;
	end if;
	end;
end if;
return qt_horas_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
