SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_perc_mercado ( nr_seq_merc_empresa_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE

/*
0 - Todos
1 - Por estado
*/
qt_total_mercado_w		integer;
qt_cliente_w			integer;
ds_retorno_w			double precision;


BEGIN

select	sum(qt_mercado)
into STRICT	qt_total_mercado_w
from	com_mercado;

if (ie_opcao_p = 0) then

	select	sum(qt_cliente)
	into STRICT	qt_cliente_w
	from	com_mercado_empresa
	where	nr_sequencia = nr_seq_merc_empresa_p;

	ds_retorno_w := dividir((qt_cliente_w * 100),qt_total_mercado_w);

elsif (ie_opcao_p = 1) then

	select	a.qt_cliente,
		b.qt_mercado
	into STRICT	qt_cliente_w,
		qt_total_mercado_w
	from	com_mercado_empresa a,
		com_mercado b
	where	a.nr_seq_mercado = b.nr_sequencia
	and	a.nr_sequencia   = nr_seq_merc_empresa_p;

	ds_retorno_w := dividir((qt_cliente_w * 100),qt_total_mercado_w);

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
