SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_quant_prop_situacao ( nr_seq_cliente_p bigint, ie_situacao_p text) RETURNS bigint AS $body$
DECLARE


qt_propostas_w	bigint := 0;


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin
	if (ie_situacao_p = 'C') then
		begin
		select	count(distinct(p.nr_sequencia))
		into STRICT	qt_propostas_w
		from	com_cliente_prop_item i,
			com_cliente_proposta p
		where	i.nr_seq_proposta = p.nr_sequencia
		and	p.nr_seq_cliente = nr_seq_cliente_p
		--and	p.dt_liberacao is not null
		and	(p.dt_cancelamento IS NOT NULL AND p.dt_cancelamento::text <> '')
		--and	nvl(p.dt_vencimento,sysdate) >= sysdate
		and	coalesce(i.ie_orcam_coml_wheb,'N') = 'S';
		end;
	elsif (ie_situacao_p = 'V') then
		begin
		select	count(distinct(p.nr_sequencia))
		into STRICT	qt_propostas_w
		from	com_cliente_prop_item i,
			com_cliente_proposta p
		where	i.nr_seq_proposta = p.nr_sequencia
		and	p.nr_seq_cliente = nr_seq_cliente_p
		--and	p.dt_liberacao is not null
		and	trunc(p.dt_vencimento,'dd') < trunc(clock_timestamp(),'dd')
		--and	nvl(p.dt_vencimento,sysdate) >= sysdate
		and	coalesce(i.ie_orcam_coml_wheb,'N') = 'S';
		end;
	end if;
	end;
end if;
return qt_propostas_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
